import React, { useState } from "react";
import { CSSTransition } from "react-transition-group";
import "./tiptoolbutton.css";

function ButtonWithTip() {
  const [showTip, setShowTip] = useState(false);

  return (
    <div className="container">
      <button className="round-button" onClick={() => setShowTip(!showTip)}>
        {"< TIP"}
      </button>
      <CSSTransition
        in={showTip}
        timeout={500}
        classNames="slide"
        unmountOnExit
      >
        <div className="tip-box">
          <p>TIP 1) 갤럭시 번역 꺼라 2) 리롤 3) 리롤 후 답변 4) Tab 키</p>
          <p>
            글자가 이상하게 나와요. <br /> chrome을 이용할 시 자동연어번역을
            꺼주세요.
          </p>
        </div>
      </CSSTransition>
    </div>
  );
}

export default ButtonWithTip;
