import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import AiIcon from "../images/AiIcon.png"; // 이미지 import
import UserIcon from "../images/UserIcon.png"; // 이미지 import
import Button from "@mui/material/Button";
import React, { useState } from "react";
import DetailOpenButton from "../images/DetailOpenButton.png"; // 이미지 import
import DetailCloseButton from "../images/DetailCloseButton.png";
import RerollButton from "../images/RerollButton.png";
import axios from "axios";

const QnA = ({
  question,
  answer,
  aiQuestion,
  borderStrength,
  index,
  borderBottomStrength,
}) => {
  const [boxes, setBoxes] = useState(false);
  const [rerolledAnswer, setRerolledAnswer] = useState(aiQuestion);

  const rerollResponse = async () => {
    try {
      const response = await axios.post(
        process.env.REACT_APP_API_URL + "/changeQuestion/",
        {
          data: question,
        }
      );
      if (response.data) {
        let savedQnas = JSON.parse(localStorage.getItem("qnas"));
        savedQnas[index].aiQuestion = response.data.response;
        setRerolledAnswer(response.data.response);
      } else {
        console.error("No data in response");
      }
    } catch (error) {
      console.error("Error rerolling response: ", error);
    }
  };
  return (
    <Box
      sx={{
        display: "flex",
        flexDirection: "column",
        width: "810px",
        // height: "50px",
      }}
    >
      <Box
        sx={{
          display: "flex",
          gap: "0px",
          alignItems: "center",
          backgroundColor: "#F3F3F3",
          width: "815px",
          minheight: "100px",
          overflowWrap: "break-word",
          wordBreak: "keep-all",
          borderTop: "0.01px solid black",
        }}
      >
        <img
          src={UserIcon}
          alt="SeaTurtle"
          width="26"
          height="27"
          style={{
            padding: "12.5px",
            marginLeft: "10px",
            marginRight: "10px",
            transform: "rotate(11deg)",
          }}
        />
        <Typography>{question}</Typography>
        <img
          src={boxes ? DetailCloseButton : DetailOpenButton}
          width="30"
          height="30"
          style={{
            display: "flex",
            position: "absolute",
            left: "770px",
          }}
          onClick={() => setBoxes(!boxes)}
        ></img>
      </Box>
      {boxes && (
        <Box
          sx={{
            display: "flex",
            gap: "0px",
            alignItems: "center",
            backgroundColor: "#F3F3F3",
            width: "815px",
            minheight: "100px",
            overflowWrap: "break-word",
            wordBreak: "keep-all",
            borderTop: "0.01px solid black",
          }}
        >
          <img
            src={UserIcon}
            alt="SeaTurtle"
            width="26"
            height="27"
            style={{
              padding: "12.5px",
              marginLeft: "10px",
              marginRight: "10px",
              transform: "rotate(11deg)",
            }}
          />
          <Typography>{rerolledAnswer}</Typography>
          <img
            src={RerollButton}
            width="23w"
            height="23"
            style={{
              display: "flex",
              position: "absolute",
              left: "773px",
            }}
            onClick={rerollResponse}
          ></img>
        </Box>
      )}

      <Box
        sx={{
          display: "flex",
          gap: "0px",
          alignItems: "center",
          backgroundColor: "white",
          width: "815px",
          minheight: "100px",

          borderTop: "0.01px solid black",
          overflowWrap: "break-word",
          wordBreak: "keep-all",
          borderBottom: `${borderBottomStrength} solid black`,
        }}
      >
        <img
          src={AiIcon}
          alt="AI"
          width="25"
          height="25"
          style={{ padding: "12.5px", marginLeft: "10px", marginRight: "10px" }}
        />
        <Typography>{answer}</Typography>
      </Box>
    </Box>
  );
};

export default QnA;
