import React, { useState } from "react";

function RatingModal() {
  const [isOpen, setIsOpen] = useState(true); // 모달이 열려 있는지 여부
  const [rating, setRating] = useState(0); // 선택된 별의 개수

  // 별점을 설정하는 함수
  const handleRating = (rate) => {
    setRating(rate);
  };

  return (
    <>
      {isOpen && (
        <div className="modal">
          <h2>평점을 입력해주세요</h2>
          <div className="stars">
            {[1, 2, 3, 4, 5].map((star) => (
              <span
                key={star}
                className={star <= rating ? "filled" : ""}
                onClick={() => handleRating(star)}
              >
                ★
              </span>
            ))}
          </div>
          <button onClick={() => setIsOpen(false)}>확인</button>
        </div>
      )}
    </>
  );
}

export default RatingModal;
