import React, { useState, useEffect } from "react";
import axios from "axios";
import QnA from "../../component/qna";
import "./page.css";

export default function Problem() {
  const [text, setText] = useState("");
  const [qnas, setQnas] = useState([]); // QnA 컴포넌트에 대한 상태를 관리하는 state
  const [question, setQuestion] = useState(""); // 문제를 저장할 상태
  const [tabPressed, setTabPressed] = useState(false);
  const [buttonColor, setButtonColor] = useState("defaultColor"); // 상태를 추가하여 버튼 색상 변경 관리

  useEffect(() => {
    axios
      .get("http://localhost:3000/problem")
      .then((response) => {
        const data = response.data;
        setQuestion(data.question + " 왜그랬을까?"); // 데이터를 상태에 저장
      })
      .catch((error) => {
        console.error("There was an error!", error);
      });
  }, []);

  const handleChange = (e) => {
    setText(e.target.value);
  };

  const handleKeyPress = (e) => {
    if (e.key === "Tab") {
      e.preventDefault();
      setTabPressed(true);
      setButtonColor("changedColor"); // 탭을 눌렀을 때 버튼 색상을 변경
    }
    if (e.key === "Enter") {
      e.preventDefault();
      tabPressed ? handleTabClick() : handleSendClick(); // 엔터를 눌렀을 때 버튼 클릭 이벤트를 트리거
    }
  };

  const handleSendClick = async () => {
    try {
      // const response = await axios.post("http://localhost:8000/api/endpoint", {
      //   text,
      // });
      // console.log(response.data); // 응답 확인
      setQnas([{ question: text, answer: text }, ...qnas]);
      setText("");
    } catch (error) {
      console.error(error);
    }
  };

  const handleTabClick = async () => {
    try {
      const response = await axios.post("http://localhost:8000/api/endpoint", {
        text,
      });
      console.log(response.data); // 응답 확인
      const result = response.data.answer;
      if (result.startsWith("네")) {
        // thanks 페이지로 이동
        window.location.href = "/thanks";
      } else {
        setText("");
        setTabPressed(false);
      }
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="all">
      <div className="e218_192">
        <input
          className="textbox"
          value={text}
          onChange={handleChange}
          onKeyPress={handleKeyPress}
        />
        <div className="ei125_118_152_168">
          <button
            className={`send_button ${buttonColor}`}
            onClick={tabPressed ? handleTabClick : handleSendClick}
          ></button>
        </div>
      </div>

      <div className="e28_163">
        <div className="e111_301">
          <span className="nickname">thisis2jun9 님</span>
          <div className="e125_157">
            <div className="profile_photo"></div>
          </div>
        </div>
        <p className="About">About</p>
        <p className="QnA">QnA</p>
        <p className="Log">Log</p>
        <div className="e125_159">
          <div className="ei125_159_144_2659"></div>
        </div>
        <span className="F22F">F22F</span>
      </div>
      <div className="e168_70">
        <span className="description">
          텍스트 입력 칸에 추측한 내용을 적으면 ‘네’ 또는 ‘아니오’ 형식의 답을
          받을 수 있습니다.
        </span>
        <span className="description_2">
          N번째 바다거북수프의 정답을 맞혀보세요.
        </span>
      </div>
      <div className="e218_180">
        <div className="e102_77">
          <div className="ei102_77_152_95"></div>
        </div>
        <span className="Question">{question}</span>
      </div>
      <div className="e218_179">
        <div className="e186_106"></div>
        <div className="e186_107">
          <div className="ei186_107_5790_3581"></div>
        </div>
        <span className="e186_108">TOP</span>
      </div>
      {qnas.map((qna, index) => (
        <div className="QAresponse" key={index}>
          <QnA
            question={qna.question}
            answer={qna.answer}
            borderStrength={index === 0 ? "2px" : "0px"}
            borderBottomStrength={index === qnas.length - 1 ? "0.01px" : "0px"}
          />
        </div>
      ))}
    </div>
  );
}
